
public class SqrtX69 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=100
				;
		int a=mySqrt(x);
		
		System.out.println(a);
	}
	public static int mySqrt(int x) {
        int y=0;
        if(x>=0){
        	
        	y=(int) Math.sqrt(x);
        	
        }
        
        return y;
    }
}
